(1, 'PASS_RESET_CODE', 'Password Reset', 'Password Reset Request', NULL, '<div><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">We received a request to reset your <b>Safer Naija account password.</b><br><br>\r\nRequested From IP: <b>{{ip}}.</b><br>\r\nUsing:<b> {{browser}}</b> on <b>{{operating_system}}. </b><br>\r\nTime: <b>{{time}}.</b><br><br>\r\nYour account recovery code is: <font size=\"6\"><b>{{code}}.</b></font>\r\n</span></div><br>\r\n\r\n\r\n<div><font size=\"3\"><font color=\"#ff3300\" style=\"\">\r\n<b>If you did not initiate this request, take these simple steps to keep your account safe.</b></font></font></div>\r\n\r\n<div><font color=\"#000000\" size=\"3\">\r\n<b>1.</b> Log in to your account and change your password.</font></div>\r\n\r\n<div><font color=\"#000000\" size=\"3\"><b>2.\r\n</b> Turn on Google Two Factor Authentication.</font></div><br>\r\n\r\n<div><font color=\"#000000\" size=\"3\">If you have any queries, kindly contact us.</font></div><br>\r\n\r\n<div><font color=\"#000000\" size=\"3\">Regards,</font></div>\r\n<div><font color=\"#000000\" size=\"3\"><b style=\"\"><b>The Safer Naija Team.</b></b></font></div>\r\n\r\nwww.safernaija.com.ng', 'Your account recovery code is: {{code}}', NULL, ' {\"code\":\"Password Reset Code\",\"ip\":\"IP of User\",\"browser\":\"Browser of User\",\"operating_system\":\"Operating System of User\",\"time\":\"Request Time\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 04:07:05'),
(2, 'PASS_RESET_DONE', 'Password Reset Confirmation', 'Password Reset Successful', NULL, '<div><font color=\"#000000\">\r\nYou have successfully reset your password.</font><br><br>\r\n\r\n<font color=\"#000000\">From IP: <b>{{ip}}.</b></font><br>\r\n<font color=\"#000000\">Using: <b>{{browser}}</b> on <b>{{operating_system}}.</b></font><br>\r\n<font color=\"#000000\"><b>Time:</b> {{time}}.</font><br><br>\r\n\r\n<font color=\"#ff3300\"><b>If you did not initiate this request, take these simple steps to keep your account safe, or contact us if you have unexpectedly lost access to your account.</b></font><br>\r\n<font color=\"#000000\"><b>1.</b> Log in to your account and change your password.</font><br>\r\n<font color=\"#000000\"><b>2.</b> Turn on Google Two Factor Authentication.</font><br><br>\r\n\r\n<font color=\"#000000\">If you have any queries, kindly contact us.</font><br><br>\r\n\r\n<font color=\"#000000\" size=\"3\">Regards,</font><br>\r\n<font color=\"#000000\"><b>The Safer Naija Team.</b></font></div>\r\n\r\nwww.safernaija.com.ng', 'Your password has been changed successfully', NULL, '{\"ip\":\"IP of User\",\"browser\":\"Browser of User\",\"operating_system\":\"Operating System of User\",\"time\":\"Request Time\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 04:07:58'),
(3, 'EVER_CODE', 'Email Verification', 'Verify Your Email Address', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nThanks for joining us.<br>\r\nPlease use the code below to verify your account on <b>Safer Naija.</b><br><br>\r\n\r\n <b>Your email verification code is:<font size=\"5\" color=\"#00cc00\"> {{code}}</font></b><br><br>\r\n\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Your email verification code is: {{code}}', NULL, '{\"code\":\"Verification code\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 03:59:45'),
(4, 'SVER_CODE', 'SMS Verification ', 'Verify Your Phone Number', NULL, 'Your phone verification code is: <b>{{code}}</b><div><br></div><div><span style=\"font-weight: bolder; color: rgb(33, 37, 41);\"><font color=\"#ff3300\">If you noticed that your phone number or email address has been used to create an account on Safer Naija without your permission, kindly contact us so we can take appropriate action against the user.</font></span></div><div><span style=\"font-weight: bolder; color: rgb(33, 37, 41);\"><font color=\"#ff3300\"><br></font></span></div><div>Regards,</div><div><b>The Safer Naija Team.</b></div>\r\n\r\nwww.safernaija.com.ng', 'Your phone verification code is: {{code}}', NULL, '{\"code\":\"Verification code\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 04:08:35'),
(5, '2FA_ENABLE', 'Google Two Factor - Enable', 'Google 2FA Enabled!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\n<b><font color=\"68d470\">Your Account is Secure!</font></b><br>\r\nYou just enabled Google Two Factor Authentication for your Account for additional security.<br><br>\r\n\r\n<b>Enabled At:</b> {{time}}.<br>\r\n<b>From IP:</b> {{ip}}.<br>\r\n<b>Using:</b> {{browser}}.<br>\r\n<b>Operating system:</b> {{operating_system}}.<br><br>\r\n\r\n<b><font color=\"#ff3300\">If you did not activate this feature on your account, contact us immediately!</font></b><br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Your verification code is: {{code}}', NULL, '{\"ip\":\"IP of User\",\"browser\":\"Browser of User\",\"operating_system\":\"Operating System of User\",\"time\":\"Request Time\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 04:00:43'),
(6, '2FA_DISABLE', 'Google Two Factor Disable', 'Google 2FA Disabled!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\n<b><font color=\"#ff3300\">Your Account is Insecure!</font></b><br>\r\nYou just Disabled Google Two Factor Authentication for Your Account, which could render your account vulnerable to cyber attacks and anyone who knows your password.<br><br>\r\n\r\n<b>Enabled At:</b> {{time}}.<br>\r\n<b>From IP:</b> {{ip}}.<br>\r\n<b>Using:</b> {{browser}}.<br>\r\n<b>Operating system:</b> {{operating_system}}.<br><br>\r\n\r\n<b><font color=\"#ff3300\">If you did not activate this feature on your account, contact us immediately!</font></b><br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Google two factor verification is disabled', NULL, '{\"ip\":\"IP of User\",\"browser\":\"Browser of User\",\"operating_system\":\"Operating System of User\",\"time\":\"Request Time\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-24 23:04:05', '2025-06-16 04:01:37'),
