(206, 'DEPOSIT_COMPLETE', 'Automated Deposit - Successful', 'Online Payment Successful & Wallet Credited', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nYour online payment was Successful, and your wallet has been credited!<br><br>\r\n\r\n<b>TRANSACTION DETAILS BELOW:</b><br><br>\r\n\r\n<b>Amount Charged:</b> {{amount}}.<br>\r\n<b>Service Charge:</b> {{charge}}.<br>\r\n<b>Total Paid:</b> {{method_amount}}.<br>\r\n<b>Paid via:</b> {{method_name}}.<br>\r\n<b>Transaction ID:</b> {{trx}}.<br><br>\r\n\r\n<b>YOUR WALLET BALANCE IS: <font color=\"#68d470\">{{post_balance}}.</font></b><br><br> \r\n\r\nIf you have any queries, kindly contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', '{{amount}} {{currrency}} Deposit successfully by {{gateway_name}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Deposit Method Name\",\"method_currency\":\"Deposit Method Currency\",\"method_amount\":\"Deposit Method Amount After Conversion\", \"post_balance\":\"Users Balance After this operation\"}', 1, 0, NULL, NULL, NULL, 0, '2020-06-24 18:00:00', '2025-06-16 03:56:47'),
(207, 'DEPOSIT_REQUEST', 'Manual Deposit - User Requested', 'Deposit Sent, Awaiting Confirmation', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nYour deposit of <b>{{amount}}</b> for your Report Promotion has been submitted successfully and is awaiting confirmation.<br><br>\r\n\r\n<b>DETAILS OF YOUR DEPOSIT</b><br><br>\r\n\r\n<b>Deposited Amount:</b> {{amount}}.<br>\r\n<b>Service Charge:</b> <font color=\"#ff3300\">{{charge}}.</font><br>\r\n<b>Payment For:</b> Report Promotion. <br><br>\r\n\r\n<b>Payable:</b> {{method_amount}}.<br>\r\n<b>Paid Via:</b> {{method_name}}.<br><br>\r\n\r\n<b>TRANSACTION ID:</b> {{trx}}.<br><br>\r\n\r\n<font color=\"#68d470\"><b>Please wait while we confirm your payment.</b></font><br><br>\r\n\r\nIf you have any queries, kindly contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', '{{amount}} Deposit requested by {{method}}. Charge: {{charge}} . Trx: {{trx}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Deposit Method Name\",\"method_currency\":\"Deposit Method Currency\",\"method_amount\":\"Deposit Method Amount After Conversion\"}', 1, 0, NULL, NULL, NULL, 0, '2020-05-31 18:00:00', '2025-06-16 04:03:29'),
(208, 'DEPOSIT_APPROVE', 'Manual Deposit - Admin Approved', 'Your Deposit is Approved!', NULL, '<div>Your deposit request of <b>{{amount}}</b>&nbsp;was <b>APPROVED!<br></b></div><div><b><br></b></div><div><b>Details of your Deposit :<br></b></div><div><br></div><div><b>Amount:&nbsp;</b><font color=\"#212529\"></font><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">{{amount}}.</span></div><div><b>Service Charge:</b> <font color=\"#ff3300\">{{charge}}.</font></div><div><br></div><div><b>Payable:</b> {{method_amount}}.<br></div><div><b>Paid via:&nbsp;</b> {{method_name}}.</div><div><b style=\"font-size: 1rem;\">Transaction ID:</b><span style=\"color: rgb(33, 37, 41); font-size: 1rem;\"> {{trx}}.</span></div><div><font size=\"5\"><b><br></b></font></div><div><font size=\"5\">Your current Balance is:&nbsp;</font><font color=\"#212529\" size=\"5\"><b></b></font><b style=\"font-size: x-large;\">{{post_balance}}</b></div><div><br></div><div>Regards,</div><div><b>The Safer Naija Team.</b><br></div>\r\nwww.safernaija.com.ng', 'Admin Approve Your {{amount}} {{gateway_currency}} payment request by {{gateway_name}} transaction : {{transaction}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Deposit Method Name\",\"method_currency\":\"Deposit Method Currency\",\"method_amount\":\"Deposit Method Amount After Conversion\", \"post_balance\":\"Users Balance After this operation\"}', 1, 0, NULL, NULL, NULL, 0, '2020-06-16 18:00:00', '2025-06-16 04:02:35'),
(209, 'DEPOSIT_REJECT', 'Manual Payment- Admin Rejected', 'Payment Request for Promotion Rejected!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nYour payment request for your Report Promotion was <b>REJECTED!</b><br><br>\r\n\r\n<b>YOUR PAYMENT REQUEST DETAILS</b><br><br>\r\n\r\n<b>Amount Was:</b> {{amount}}.<br>\r\n<b>Requested Via:</b> {{method_name}}.<br><br>\r\n\r\n<b>TRANSACTION ID:</b> {{trx}}.<br><br>\r\n\r\n<b>Reason for Rejection: <font color=\"#ff3300\">{{rejection_message}}</font></b><br><br>\r\n\r\n<b>Refund Instructions:</b>&nbsp;For safety reasons, we refund money only to the user\'s Wallet Balance, not to their bank account<br><br>\r\n\r\nIf you have any queries, feel free to contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b>\r\n<br>\r\nwww.safernaija.com.ng\r\n</span>', 'Admin Rejected Your {{amount}} {{gateway_currency}} payment for ad promotion request by {{gateway_name}}\r\n\r\n{{rejection_message}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Deposit Method Name\",\"method_currency\":\"Deposit Method Currency\",\"method_amount\":\"Deposit Method Amount After Conversion\",\"rejection_message\":\"Rejection message\"}', 1, 0, NULL, NULL, NULL, 0, '2020-06-09 18:00:00', '2025-06-16 04:05:38'),
(215, 'BAL_ADD', 'Balance Add by Admin', 'Credit Alert!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\"> \r\n<b>{{amount}}</b> has been credited to your wallet balance.<br><br>\r\n\r\n<b>Sent From: </b> Safer Naija.<br>\r\n<b>Transaction ID:</b> {{trx}}.<br><br>\r\n\r\n<b>YOUR WALLET BALANCE IS: <font color=\"68d470\"> {{post_balance}}.</font></b><br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', '{{amount}} {{currency}} credited in your account. Your Current Balance {{remaining_balance}} {{currency}} . Transaction: #{{trx}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By Admin\",\"currency\":\"Site Currency\", \"post_balance\":\"Users Balance After this operation\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-14 19:14:22', '2025-06-16 03:57:37'),
(216, 'BAL_SUB', 'Balance Subtracted by Admin', 'Debited Alert!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\"> \r\n<b>{{amount}}</b> has been debited from your wallet balance.<br><br>\r\n\r\n<b>Debit Initiated By: </b> Safer Naija.<br>\r\n<b>Transaction ID:</b> {{trx}}.<br><br>\r\n\r\n<b>YOUR WALLET BALANCE IS: <font color=\"#ff3300\"> {{post_balance}}.</font></b><br><br>\r\n\r\n<font color=\"68d470\"><b>If you don\'t know why you were debited, kindly log in to your Dashboard to see the reason for this debit.</b></font><br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', '{{amount}} {{currency}} debited from your account. Your Current Balance {{remaining_balance}} {{currency}} . Transaction: #{{trx}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By Admin\",\"currency\":\"Site Currency\", \"post_balance\":\"Users Balance After this operation\"}', 1, 0, NULL, NULL, NULL, 0, '2019-09-14 19:14:22', '2025-06-16 03:58:41'),
(217, 'PROMOTION_REQUEST', 'Ad promotion request - User ', 'Payment Confirmed For Report Promotion', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nYour payment for your report promotion has been confirmed.<br><br>\r\n\r\n<b>PAYMENT AND PROMOTION DETAILS </b><br><br>\r\n\r\n<b>Report To Promote:</b> {{ad_title}}.<br>\r\n<b>Package:</b> {{package}}.<br>\r\n<b>Promotion Validity:</b> {{validity}} days.<br>\r\n<b>Amount :</b> {{amount}}.<br>\r\n<b>Service Charge:</b> <font color=\"#ff3300\"> {{charge}}. </font><br><br>\r\n\r\n<b>Payable:</b> {{method_amount}}.<br>\r\n<b>Paid Via:</b> {{method_name}}.<br><br>\r\n\r\n<b>TRANSACTION ID:</b> {{trx}}.<br><br>\r\n\r\n<font color=\"#68d470\"><b>Please wait while we Review and Promote your Report according to your package choice. We will keep you updated via WhatsApp.</b></font><br><br>\r\n\r\nIf you have any queries, kindly contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', '{{amount}} Ad promotion requested by {{method}}. Charge: {{charge}} . Trx: {{trx}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Deposit Method Name\",\"method_currency\":\"Deposit Method Currency\",\"method_amount\":\"Deposit Method Amount After Conversion\",\"ad_title\":\"ad title\",\"package\":\"package name\",\"validity\":\"promotion validity\"}', 1, 0, NULL, NULL, NULL, 0, '2020-05-31 18:00:00', '2025-06-16 03:56:08'),
(218, 'PROMOTION_APPROVE', 'Ad promotion Approve- User ', 'Report Promotion Request Approved!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nCongratulations!<br>\r\nYour report promotion request has been approved and will run for {{validity}}.<br><br>\r\n\r\n <b>REPORT PROMOTION DETAILS</b><br><br>\r\n\r\n<b>Promoted Report:</b> {{ad_title}}.<br>\r\n<b>Chosen Package:</b> {{package}}.<br>\r\n<b>Valid For:</b> {{validity}} days.<br>\r\n<b>Amount Paid:</b> {{amount}}.<br>\r\n<b>Paid Via:</b> Wallet Balance.<br><br>\r\n\r\nIf you have queries, kindly contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Ad promotion accepted reviewer Ad title {{ad_title}}. Package {{package}}, Validity {{validity}} days', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"charge\":\"Gateway Charge\",\"currency\":\"Site Currency\",\"rate\":\"Conversion Rate\",\"method_name\":\"Payment Method Name\",\"method_currency\":\"Payment Method Currency\",\"method_amount\":\"Payment Method Amount After Conversion\",\"ad_title\":\"ad title\",\"package\":\"package name\",\"validity\":\"promotion validity\"}', 1, 0, NULL, NULL, NULL, 0, '2020-05-31 18:00:00', '2025-06-16 03:50:16'),
(219, 'PROMOTE_REJECT', 'Ad Promotion reject - Admin Rejected', 'Report promotion Rejected, and Deposit Refunded!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\n<b>Your report promotion request was rejected!</b><br>\r\nPlease find the reason below and make the necessary corrections before resending your request.<br><br>\r\n\r\n<b>Report To Promote:</b> {{ad_title}}.<br>\r\n<b>Package:</b> {{package}}.<br>\r\n<b>Deposited Amount:</b> {{amount}}<br>\r\n<b>Paid Via:</b> {{method_name}}.<br>\r\n<b>Transaction ID:</b> {{trx}}.<br><br>\r\n\r\n<b>Reason For Rejection: <font color=\"#ff3300\">{{rejection_message}}</font></b><br><br>\r\n\r\n<b>Refund Instruction:</b><b><font color=\"68d470\"> A total sum of <b> {{amount}}</b> has been refunded to your Wallet Balance, which can be used to resend your request.</font> </b><br><br>\r\n\r\nIf you have any queries, feel free to contact us.<br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Admin Rejected Your {{amount}} {{gateway_currency}} ad promotion request by {{gateway_name}}\r\n\r\n{{rejection_message}}', NULL, '{\"trx\":\"Transaction Number\",\"amount\":\"Request Amount By user\",\"currency\":\"Site Currency\",\"method_name\":\"Deposit Method Name\",\"rejection_message\":\"Rejection message\",\"ad_title\":\"ad title\",\"package\":\"package name\"}', 1, 0, NULL, NULL, NULL, 0, '2020-06-09 18:00:00', '2025-06-16 03:53:14'),
(220, 'PROMOTE_END', 'Ad Promotion expired', 'Your Report promotion expired!', NULL, '<span style=\"color: rgb(33, 37, 41); font-size: 1rem;\">\r\nThis is to notify you that your Report Promotion has expired.<br><br>\r\n\r\n<b>REPORT PROMOTION DETAILS</b><br><br>\r\n\r\n<b>Promoted Report:</b> {{ad_title}}.<br>\r\n<b>Package:</b> {{package}} <font color=\"#ff3300\"><b>(EXPIRED).</b></font><br>\r\n<b>Promoted For:</b> {{validity}} days <font color=\"#ff3300\"><b>(EXPIRED).</b></font><br><br>\r\n\r\n<font color=\"#68d470\"><b>If you are not satisfied with how this Report Promotion went, feel free to leave us a message on our contact page or on WhatsApp so we can know how to improve our services to serve you better.</b></font><br><br>\r\n\r\nRegards,<br>\r\n<b>The Safer Naija Team.</b><br>\r\nwww.safernaija.com.ng\r\n</span>', 'Your ad promotion  has been expired.\r\n\r\nAd title : {{ad_title}}\r\nPackage : {{package}}\r\nValidity was : {{validity}} days\r\n\r\nif you have any query, feel free to contact us.', NULL, '{\"ad_title\":\"Ad title\",\"package\":\"package name\",\"validity\":\"promotion validity\"}', 1, 0, NULL, NULL, NULL, 0, '2020-06-09 18:00:00', '2025-06-16 03:51:44'),
